/*Table structure for table `t_goods` */

DROP TABLE IF EXISTS `t_goods`;

CREATE TABLE `t_goods` (
  `g_id` int(11) NOT NULL AUTO_INCREMENT,
  `g_name` varchar(20) DEFAULT NULL,
  `artistid` int(11) DEFAULT NULL,
  `g_width` varchar(20) DEFAULT NULL,
  `g_height` varchar(20) DEFAULT NULL,
  `price` int(11) DEFAULT NULL,
  `colorid` int(11) DEFAULT NULL,
  `spaceid` int(11) DEFAULT NULL,
  `spaceStyleid` int(11) DEFAULT NULL,
  `artClassid` int(11) DEFAULT NULL,
  `artStyleid` int(11) DEFAULT NULL,
  `artThemeid` int(11) DEFAULT NULL,
  `startTime` varchar(20) DEFAULT NULL,
  `material` varchar(20) DEFAULT NULL,
  `g_state` int(11) DEFAULT NULL,
  `goodsSrc` varchar(30) DEFAULT NULL,
  PRIMARY KEY (`g_id`),
  KEY `FK_Reference_22` (`artistid`),
  KEY `FK_Reference_3` (`colorid`),
  KEY `FK_Reference_4` (`spaceid`),
  KEY `FK_Reference_5` (`spaceStyleid`),
  KEY `FK_Reference_6` (`artClassid`),
  KEY `FK_Reference_7` (`artStyleid`),
  KEY `FK_Reference_8` (`artThemeid`),
  CONSTRAINT `FK_Reference_22` FOREIGN KEY (`artistid`) REFERENCES `t_artist` (`artistid`),
  CONSTRAINT `FK_Reference_3` FOREIGN KEY (`colorid`) REFERENCES `t_color` (`colorid`),
  CONSTRAINT `FK_Reference_4` FOREIGN KEY (`spaceid`) REFERENCES `t_space` (`spaceid`),
  CONSTRAINT `FK_Reference_5` FOREIGN KEY (`spaceStyleid`) REFERENCES `t_spacestyle` (`spaceStyleid`),
  CONSTRAINT `FK_Reference_6` FOREIGN KEY (`artClassid`) REFERENCES `t_artclass` (`artClassid`),
  CONSTRAINT `FK_Reference_7` FOREIGN KEY (`artStyleid`) REFERENCES `t_artstyle` (`artStyleid`),
  CONSTRAINT `FK_Reference_8` FOREIGN KEY (`artThemeid`) REFERENCES `t_arttheme` (`artThemeid`)
